type.raw:
customPropTypes.every([
  PropTypes.node,
  customPropTypes.disallow(['label']),
  customPropTypes.givenProps(
    {
      icon: PropTypes.oneOfType([
        PropTypes.string.isRequired,
        PropTypes.object.isRequired,
        PropTypes.element.isRequired,
      ]),
    },
    customPropTypes.disallow(['icon']),
  ),
])

customPropTypes.every([
  PropTypes.bool,
  // these props wrap the image in an a tag already
  customPropTypes.disallow(['href']),
])

customPropTypes.every([
  customPropTypes.demand(['control']),
  // don't strictly validate HTML types
  // a control might be passed that uses a `type` prop with unknown values
  // let the control validate if for us
])

customPropTypes.every([
  customPropTypes.disallow(['children']),
  PropTypes.arrayOf(PropTypes.shape({
    key: PropTypes.string,
    active: PropTypes.bool,
    title: customPropTypes.contentShorthand,
    content: customPropTypes.contentShorthand,
    onClick: PropTypes.func,
  })),
])

defaultValue.value:
({ image, price, title, description }) => [
  image && <div key='image' className='image'>{createHTMLImage(image)}</div>,
  <div key='content' className='content'>
    {price && <div className='price'>{price}</div>}
    {title && <div className='title'>{title}</div>}
    {description && <div className='description'>{description}</div>}
  </div>,
]

